export default function ImagePopup({card, onClose, isOpen}) {

    return(

      <div className={`popup popup-expand ${isOpen && 'popup_opened'}`}>
        <div className="popup__expand-image">
          <button
            className="popup__button-close"
            type="button"
            aria-label="Закрыть"
            onClick={onClose}
          />
          <figure 
            className="picture">
            <img 
              className="picture__image"
              src={card.link}
              alt={card.name}
            />
            <figcaption className="picture__caption">
              {card.name}
            </figcaption>
          </figure>
        </div>
      </div>
    )
}
