import { useEffect, useState } from "react"
import api from "../../utils/api"
import Card from "../Card/Card"

export default function Main({onEditAvatar, onEditProfile, onAddPlace, onCardClick}) {


    const [UserName, setUserName] = useState('')
    const [UserDescription, setUserDescription] = useState('')
    const [UserAvatar, setUserAvatar] = useState('')
    const [Cards, setCards] = useState([''])

    //console.log(Cards)
    useEffect(() => {

        //Вывод массива карточек с сервера
Promise.all([api.getInfo(), api.getPicture()])
.then(([infoUser, infoPicture]) => {
  //console.log(infoUser)
  setUserName(infoUser.name)
  setUserDescription(infoUser.about)
  setUserAvatar(infoUser.avatar)
  infoPicture.forEach(element => element.writedId = infoUser._id);
  setCards(infoPicture)
}) 
.catch((error => console.log('Ошибка при загрузке карточек с сервера', error)))
//console.log(infoPicture)
    },[])
    

    return(
        <main className="content">
            <section className="profile">
            <button type="button" className="profile__avatar-button" onClick={onEditAvatar}>
            <img className="profile__avatar" src={UserAvatar} alt="Аватар профиля" />
            </button>
            <div className="profile__info">
                <div className="profile__container">
                    <h1 className="profile__title" >{UserName} </h1>
                        <button
                        className="profile__button-edit"
                        type="button"
                        aria-label="Редактировать профиль"
                        onClick={onEditProfile}
                        />
                </div>
                <p className="profile__about" >{UserDescription} </p>
            </div>
            <button
            className="profile__button-add"
            type="button"
            aria-label="Добавить место"
            onClick={onAddPlace}
            />
            </section>
            <section className="elements">
            
                {Cards.map(info => {
                   
                    return (
                        <div className="key" key = {info._id} >
                            <Card card={info} onCardClick={onCardClick}/>
                        </div>     
                    )
                    
                })}

            </section>
        </main>

    )
}
