//import logo from './logo.svg';
import Header from "./Header/Header";
import Main from "./Main/Main";
import Footer from "./Footer/Footer";
import PopupWithForm from "./popupWithForm/PopupWIthForm";
import ImagePopup from "./ImagePopup/ImagePopup";
import { useState } from "react";
import Card from "./Card/Card";


function App() {



const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false)
const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false)
const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false)
const [isSelectedCard, setSelectedCard] = useState({})


   function handleEditAvatarClick(){
    setIsEditAvatarPopupOpen(true)
   }

  function handleEditProfileClick(){
    setIsEditProfilePopupOpen(true)
  }  

  function handleAddPlaceClick(){
    setIsAddPlacePopupOpen(true)
 }  

 function closeAllPopup (){
  setIsEditAvatarPopupOpen(false)
  setIsEditProfilePopupOpen(false)
  setIsAddPlacePopupOpen(false)

 }

  // function handleDeleteClick(){

  // }

  function handleCardClick(card){
    setSelectedCard(card)
    setImagePopup(true)// DELLEETTEE
    setEventListenerDocument()
  }

  return (
    <>
  <Header/>

  <Main
    onAvatar = {handleEditAvatarClick}
    onEditProfile = {handleEditProfileClick}
    onAddPlace = {handleAddPlaceClick}

  />

  <Footer/>

  <PopupWithForm name='edit_profile'
  title='Редактировать профиль'
  isOpen = {isEditProfilePopupOpen}
  //subtitle='Сохранить'
  onClose = {closeAllPopup}
  >
<div className="popup__content">
          <input
            className="popup__input popup__input_type_name"
            name="name"
            type="text"
            id="name"
            minLength={2}
            maxLength={40}
            placeholder="Имя"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_name" />
        </div>
        <div className="popup__content">
          <input
            className="popup__input popup__input_type_job"
            name="job"
            type="text"
            id="job"
            minLength={2}
            maxLength={200}
            placeholder="О себе"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_job" />
        </div>
  </PopupWithForm>

<PopupWithForm name='add_card'
  title='Новое место'
  subtitle='Создать'
  isOpen = {isAddPlacePopupOpen}
  onClose = {closeAllPopup}
  >
<div className="popup__content">
          <input
            className="popup__input popup__input_type_place"
            name="place"
            type="text"
            id="place"
            minLength={2}
            maxLength={30}
            placeholder="Название"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_place" />
        </div>
        <div className="popup__content">
          <input
            className="popup__input popup__input_type_url"
            name="link"
            type="url"
            id="link"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_link" />
        </div>


</PopupWithForm>

<PopupWithForm name='edit_avatar'
  title='Обновить аватар'
  //subtitle='Сохранить'
  isOpen = {isEditAvatarPopupOpen}
  onClose = {closeAllPopup}
  >
<div className="popup__content">
          <input
            className="popup__input popup__input_type_url"
            name="avatar"
            type="url"
            id="avatar"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_avatar" />
        </div>


  </PopupWithForm>

<PopupWithForm name='delete'
  title='Вы уверены?'
  subtitle='Да'
  />




  {/* Открытие карточки - изоражение из карточки*/}
  <ImagePopup/>

</>

  );
}

export default App;
