function ImagePopup({ cardPic, onClose, isOpen }) {

  return (

    <div className={`popup popup-expand ${isOpen && 'popup_opened'}`} onClick={onClose}>
      <div className="popup__expand-image" onClick={(evt => evt.stopPropagation())}>
        <button
          className="popup__button-close"
          type="button"
          aria-label="Закрыть"
          onClick={onClose}
        />
        <figure
          className="picture">
          <img
            className="picture__image"
            src={cardPic.link}
            alt={cardPic.name}
          />
          <figcaption className="picture__caption">
            {cardPic.name}
          </figcaption>
        </figure>
      </div>
    </div>
  )
}

export default ImagePopup;
