//import logo from './logo.svg';
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from "./ImagePopup";
import PopupWithForm from "./PopupWIthForm";

import { useState } from "react";
//import Card from "./Card";

function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);//начальное значение скрытое
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);//начальное значение скрытое
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);//начальное значение скрытое
  const [selectedCard, setSelectedCard] = useState({});
  const [isPictureOnPopup, setIsPictureOnPopup] = useState(false);//начальное значение скрытое
  //console.log(isSelectedCard)

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }

  function handleCardClick(card) {
    setSelectedCard(card)
    setIsPictureOnPopup(true)
  }

  function closeAllPopup() {
    setIsEditAvatarPopupOpen(false)
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsPictureOnPopup(false)
  }
  return (
    <>
      <Header />

      <Main
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onCardClick={handleCardClick}
      />

      <Footer />

      <PopupWithForm
        name='edit_profile'
        title='Редактировать профиль'
        isOpen={isEditProfilePopupOpen}
        subtitle='Сохранить'
        onClose={closeAllPopup}
      >
        <div className="popup__content">
          <input
            className="popup__input popup__input_type_name"
            name="name"
            type="text"
            id="name"
            minLength={2}
            maxLength={40}
            placeholder="Имя"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_name" />
        </div>
        <div className="popup__content">
          <input
            className="popup__input popup__input_type_job"
            name="job"
            type="text"
            id="job"
            minLength={2}
            maxLength={200}
            placeholder="О себе"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_job" />
        </div>
      </PopupWithForm>

      <PopupWithForm
        name='add_card'
        title='Новое место'
        subtitle='Создать'
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopup}
      >
        <div className="popup__content">
          <input
            className="popup__input popup__input_type_place"
            name="place"
            type="text"
            id="place"
            minLength={2}
            maxLength={30}
            placeholder="Название"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_place" />
        </div>
        <div className="popup__content">
          <input
            className="popup__input popup__input_type_url"
            name="link"
            type="url"
            id="link"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_link" />
        </div>

      </PopupWithForm>

      <PopupWithForm
        name='edit_avatar'
        title='Обновить аватар'
        subtitle='Сохранить'
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopup}
      >
        <div className="popup__content">
          <input
            className="popup__input popup__input_type_url"
            name="avatar"
            type="url"
            id="avatar"
            placeholder="Ссылка на картинку"
            required=""
          />
          <span className="popup__span-error popup__span-error_type_avatar" />
        </div>

      </PopupWithForm>

      <PopupWithForm
        name='delete'
        title='Вы уверены?'
        subtitle='Да'
      />

      {/* Открытие карточки - изоражение из карточки*/}
      <ImagePopup
        cardPic={selectedCard}
        onClose={closeAllPopup}
        isOpen={isPictureOnPopup}
      />

    </>
  );
}

export default App;
