//import logo from './logo.svg';
import api from "../utils/api";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from "./ImagePopup";
import PopupWithForm from "./PopupWIthForm";

import { useCallback, useEffect, useState } from "react";
import CurrentUserContext from "../context/CurrentUserContexts";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import Info from "./Info";

//import { useState } from "react";
//import Card from "./Card";





import { Navigate, Route, Routes, useNavigate } from 'react-router-dom'
//import DeletePopup from './DeletePopup/DeletePopup'
import ProtectedRoute from "./ProtectedRoute";
import { getUserData } from '../utils/auth'
//import SendContext from '../contexts/SendContext'
import { authorization } from "../utils/auth"
import { auth } from '../utils/auth'





function App() {
  const navigate = useNavigate()
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);//начальное значение скрытое
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);//начальное значение скрытое
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);//начальное значение скрытое
  const [selectedCard, setSelectedCard] = useState({});
  const [isPictureOnPopup, setIsPictureOnPopup] = useState(false);//начальное значение скрытое
  const [isDeletePopupOpen, setIsDeletePopupOpen] = useState(false);//начальное значение скрытое
  // const [isLiked, setIsLiked] = useState(false);
  // const [setupCard, setSetupCard] = useState(true)
  const [isSuccessful, setIsSuccessful] = useState(false)
  const [isError, setIsError] = useState(false)
  const [loggedIn, setLoggedIn] = useState(false)
  const [cards, setCards] = useState([]);
  const [idCardForErase, setIdCardForErase] = useState('');
  const [currentUser, setCurrentUser] = useState({})
  const [dataUser, setDataUser] = useState('')
  //console.log(isSelectedCard)

  function handleUpdataUser(data, eraseInpup) {

    console.log(data)
    api.setInfoonServer(data)
      .then(res => {
        setCurrentUser(res)

        closeAllPopupForm()

        eraseInpup()
      })
      .catch((error => console.log('Ошибка при загрузке данных пользователя на сервера', error)))
      .finally(() => closeAllPopup(false))

  }

  function handleUpdataAvatar(data, eraseInpup) {

    api.setAvataronServer(data)
      .then(res => {
        setCurrentUser(res)

        closeAllPopupForm()

        eraseInpup()
      })
      .catch((error => console.log('Ошибка при загрузке данных аватара на сервера', error)))
      .finally(() => closeAllPopupForm(false))

  }

  function handleAddPlaceSubmit(data, eraseInpup) {

    api.addCardonServer(data)
      .then(res => {
        setCards([res, ...cards])
        closeAllPopupForm()
        eraseInpup()
      })
      .catch((error => console.log('Ошибка при загрузке данных карточки на сервера', error)))
      .finally(() => closeAllPopupForm(false))

  }


  useEffect(() => {
    if (localStorage.jwt) {
      getUserData(localStorage.jwt)
        .then(res => {
          setDataUser(res.data.email)
          setLoggedIn(true)
          navigate('/')
        })
        .catch(err => console.log(`Ошибкак авторизации повторном входе ${err}`))
    }
  }, [navigate])

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
    listnerEscape()
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
    listnerEscape()
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
    listnerEscape()
  }

  function handleCardClick(card) {
    setSelectedCard(card)
    setIsPictureOnPopup(true)
    listnerEscape()
  }

  const closeAllPopup = useCallback(() => {
    setIsEditAvatarPopupOpen(false)
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsPictureOnPopup(false)
    setIsDeletePopupOpen(false)
    setIsSuccessful(false)
    setIsError(false)
  }, [])

  const closeByEscape = useCallback((evt) => {
    if (evt.key === 'Escape') {
      closeAllPopup()
      document.removeEventListener('keydown', closeByEscape)

    }

  }, [closeAllPopup]

  )

  function handleDeleteClick(IDofCard) {
    setIsDeletePopupOpen(true)
    listnerEscape()
    setIdCardForErase(IDofCard)
  }

  function handleCardDelete(evt) {
    evt.preventDefault()
    api.eraseCardonServer(idCardForErase)
      .then(res => {
        setCards(cards.filter(items => {
          return items._id !== idCardForErase
        }))
        //console.log(res)
        closeAllPopupForm()
      })
      .catch((err) => console.log('Ошибка удаления карточки'))
      .finally(() => closeAllPopupForm(false))
  }

//  function handleCardLike(card) {
//    // Снова проверяем, есть ли уже лайк на этой карточке
//    const isLiked = card.likes.some(i => i._id === currentUser._id);
//
//    // Отправляем запрос в API и получаем обновлённые данные карточки
//    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
//      setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
//    });
//  }

  function listnerEscape() {
    document.addEventListener('keydown', closeByEscape)
  }

  const closeAllPopupForm = useCallback(() => {
    closeAllPopup()
    document.removeEventListener('keydown', closeByEscape)
  }, [closeAllPopup, closeByEscape])


  //console.log(cards)

  useEffect(() => {
    if (loggedIn) {

    //setSetupCard(true)
    //Вывод массива карточек с сервера
    Promise.all([api.getInfo(), api.getPicture()])
      .then(([infoUser, infoPicture]) => {
        //console.log(infoPicture)
        setCurrentUser(infoUser)
        setCards(infoPicture)

        //setSetupCard(false )
      })
      .catch((error => console.log('Ошибка при загрузке карточек с сервера', error)))
    }
    //console.log(infoPicture)
  }, [loggedIn])
/*
  useEffect(() => {
    if (loggedIn) {
      setIsLoadingCards(true)
      Promise.all([api.getInfo(), api.getCards()])
        .then(([dataUser, dataCards]) => {
          setCurrentUser(dataUser)
          setCards(dataCards)
          setIsLoadingCards(false)
        })
        .catch((err) => console.error(`Ошибка при загрузке начальных данных ${err}`))
    }
  }, [loggedIn])
*/




  function handleLogin(password, email) {

    authorization(password, email)
      .then(res => {
        localStorage.setItem('jwt', res.token)
        setLoggedIn(true)
        navigate('/')
      })
      .catch(err => {
        setIsError(true)
        console.error(`Ошибкак при авторизации ${err}`)
      })
      .finally()
  }

  function handleRegister(password, email) {

    auth(password, email)
      .then(res => {
        setIsSuccessful(true)
        navigate('/sign-in')
      })
      .catch((err) => {
        setIsError(true)
        console.error(`Ошибкак при регистрации ${err}`)
      })
      .finally()
  }





  return (
    <CurrentUserContext.Provider value={currentUser}>
      <>
        {/*<Header />

        <Main
          onEditAvatar={handleEditAvatarClick}
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onCardClick={handleCardClick}
          onDeletePlace={handleDeleteClick}
//          onCardLike={handleCardLike}
          cards={cards}
  />*/}


<SendContext.Provider>
          <Routes>
            <Route path='/' element={<ProtectedRoute
              dataUser={dataUser}
              openCard={handleAddPlaceClick}
              openProfile={handleEditProfileClick}
              openAvatar={handleEditAvatarClick}
              openDelete={handleDeletePopupClick}
              onCardClick={handleCardClick}
              onCardLike={handleLike}
              cards={cards}

              loggedIn={loggedIn} />
            } />
            <Route path='/sign-up' element={
              <>
                <Header name='signup' />
                <Main name='signup' handleRegister={handleRegister} />
              </>
            } />
            <Route path='/sign-in' element={
              <>
                <Header name='signin' />
                <Main name='signin' handleLogin={handleLogin} />
              </>
            } />
            <Route path='*' element={<Navigate to='/' />} />
          </Routes>
        </SendContext.Provider>





        <Footer />

        <EditProfilePopup
          onUpdateUser={handleUpdataUser}
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopupForm}

        />

        <AddPlacePopup
          onAddPlace={handleAddPlaceSubmit}
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopupForm}
        />


        <EditAvatarPopup

          onUpdateAvatar={handleUpdataAvatar}
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopupForm}
        />

        <PopupWithForm
          name='delete'
          title='Вы уверены?'
          subtitle='Да'
          isOpen={isDeletePopupOpen}
          onClose={closeAllPopupForm}
          onSubmit={handleCardDelete}
        />

        {/* Открытие карточки - изоражение из карточки*/}
        <ImagePopup
          cardPic={selectedCard}
          onClose={closeAllPopupForm}
          isOpen={isPictureOnPopup}
        />



        <Info
          name='successful'
          titleText={'Вы успешно зарегистрировались!'}
          isOpen={isSuccessful}
          onClose={closeAllPopupForm}
        />

        <Info
          name='error'
          titleText={'Что-то пошло не так! Попробуйте ещё раз.'}
          isOpen={isError}
          onClose={closeAllPopupForm}
        />    

      </>
    </CurrentUserContext.Provider>
  );

}

export default App;
